<?php

namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => 'dev-master',
    'version' => 'dev-master',
    'aliases' => 
    array (
    ),
    'reference' => '6825cc725ee7589a190499e87eaaccab51f94e31',
    'name' => 'automad/lib',
  ),
  'versions' => 
  array (
    'automad/lib' => 
    array (
      'pretty_version' => 'dev-master',
      'version' => 'dev-master',
      'aliases' => 
      array (
      ),
      'reference' => '6825cc725ee7589a190499e87eaaccab51f94e31',
    ),
    'jbroadway/urlify' => 
    array (
      'pretty_version' => '1.1.0-stable',
      'version' => '1.1.0.0',
      'aliases' => 
      array (
      ),
      'reference' => '99bb78cd9002d0e9ce479bb81635eb665e37e981',
